mapper.select:
SELECT matching_id, user1_id, user2_id, matching_status, matching_start, matching_finish
FROM matchings
WHERE user1_id = #{userId} OR user2_id = #{userId}
ORDER BY matching_start DESC
    LIMIT #{limit}

SELECT matching_status
FROM matchings
WHERE matching_id = #{matchingId}

mapper.update:
UPDATE matchings
SET matching_status = #{matchingStatus}
WHERE matching_id = #{matchingId}
