mapper.select:
SELECT matching_id, user1_id, round_number, user2_id, matching_status
FROM matchings
WHERE user1_id = #{userId} OR user2_id = #{userId}
ORDER BY round_number DESC
LIMIT #{limit}

mapper.update:
UPDATE matchings
SET matching_status = 'COMPLETED'
WHERE matching_status = 'PROGRESS';

