mapper.select:
SELECT score_value
FROM scores
WHERE user_id = #{userId} AND round_number = #{roundNumber}

mapper.update:
UPDATE scores
SET score_value = #{scoreValue}, update_date = NOW()
WHERE user_id = #{userId} AND round_number = #{roundNumber}

mapper.select:
SELECT round_number, user_id, score_value, reg_date, update_date
FROM scores
WHERE round_number = #{roundNumber}
ORDER BY score_value DESC
LIMIT #{limit}
