mapper.select:
SELECT score_value
FROM scores
WHERE user_id = #{userId} AND round_number = #{roundNumber}

SELECT round_number, user_id, score_value, is_level_up, reg_date, update_date
FROM scores
WHERE round_number = #{roundNumber}

SELECT round_number, user_id, score_value, is_level_up, reg_date, update_date
FROM scores
WHERE round_number = #{roundNumber}
ORDER BY score_value DESC
LIMIT #{limit}
